import React, { useState } from "react";

function BasicReport(props) {
  const [order, setOrder] = useState("default");
  const { data } = props;

  const sortTable = (props) => {
    console.log(props);
    let nextOrder;
    if (order === "default") {
      nextOrder = "desc";
    }
    if (order === "desc") {
      nextOrder = "asc";
    }
    if (order === "asc") {
      nextOrder = "default";
    }

    setOrder(nextOrder);
  };

  return (
    <div class="container my-4">
      {/* Filters */}
      <form>
        <div class="row">
          <div class="col">
            <input type="text" class="form-control" placeholder="First name" />
          </div>
          <div class="col">
            <input type="text" class="form-control" placeholder="Last name" />
          </div>
        </div>
      </form>

      {/* Report */}
      <div class="container p-4">
        <table class="table" id="report">
          <thead class="thead-dark">
            <tr>
              <th scope="col" onClick={sortTable}>
                Código
              </th>
              <th scope="col" onClick={sortTable}>
                Nombre
              </th>
              <th scope="col" onClick={sortTable}>
                Apellido
              </th>
              <th scope="col" onClick={sortTable}>
                Segmento
              </th>
              <th scope="col" onClick={sortTable}>
                Fecha de ingreso
              </th>
            </tr>
          </thead>
          <tbody>
              {[...data].sort(orderTypes.order.fn).map((student) => 
              <tr>
              <td>{student.code}</td>
              <td>{student.name}</td>
              <td>{student.lastname}</td>
              <td>{student.segment}</td>
              <td>{student.entryDate}</td>
            </tr>
              )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

const orderTypes = {
  desc: {
    class: "sort-up",
    fn: (a, b) => a.net_worth - b.net_worth,
  },
  asc: {
    class: "sort-down",
    fn: (a, b) => b.net_worth - a.net_worth,
  },
  default: {
    class: "sort",
    fn: (a, b) => a,
  },
};

export default BasicReport;
