      console.log(props)
    // if (e.target.dataset.order === 'desc') {
    //     setOrder("asc")
    // }
    console.log('Mofificado', order);
  }

  return (
    <div class="container my-4">
      {/* Filters */}
      <form>
        <div class="row">
          <div class="col">
            <input type="text" class="form-control" placeholder="First name" />
          </div>
          <div class="col">
            <input type="text" class="form-control" placeholder="Last name" />
          </div>
        </div>
      </form>

      {/* Report */}
      <div class="container p-4">
        <table class="table" id="report">
          <thead class="thead-dark">
            <tr>
              <th
                scope="col"
                data-column="code"
                data-order={order}
                onClick={sortTable}
              >
                CÃ³digo
              </th>
              <th
                scope="col"
                data-column="name"
                data-order={order}
                onClick={sortTable}
              >
                Nombre
              </th>
              <th
                scope="col"
                data-column="lastname"
                data-order={order}
                onClick={sortTable}
              >
                Apellido
              </th>
              <th
                scope="col"
                data-column="segment"
                data-order={order}
                onClick={sortTable}
              >
                Segmento
              </th>
              <th
                scope="col"
                data-column="entry-date"
                data-order={order}
                onClick={sortTable}
              >
                Fecha de ingreso
              </th>
            </tr>
          </thead>
          <tbody>
              {data.map(student => (
                  <tr>
                      <td>{student.code}</td>
                      <td>{student.name}</td>
                      <td>{student.lastname}</td>
                      <td>{student.segment}</td>
                      <td>{student.entryDate.toString()}</td>
                  </tr>
              ))}

            {/* // <tr>
            //   <th scope="row">1</th>
            //   <td>Mark</td>
